import React,{useRef,useState,useEffect} from 'react'
import { IoDocumentAttach } from 'react-icons/io5'
import { sendMail } from '../api'
import FileBase from 'react-file-base64';

const Form = () => {
    const [file, setFile] = useState()
    const [email, setEmail] = useState("")
    const [subject,setSubject] = useState("No Subject")
    const [message, setMessage] = useState("")
    const [validated,setValidated] = useState(false)

    const inputFile = useRef(null)

    const handleClick = e => {
        inputFile.current.click()
      }

    const handleOnChange = e => {
        setFile(e.target.files[0])        
    }

    const isValidate = (email) =>{
        return /\S+@\S+\.\S+/.test(email);
    }
    
    const validateEmail = () => {
        if (!isValidate(email)) {
            setValidated(false)
          } else {
            setValidated(true)
          }
    }

    const handleSend = async (e) => {
      const mail = {email:email,subject:subject,message:message,file:file}
      if(validated){
        try {
          const {data,status} = await sendMail(mail)
          console.log(data + " " + status)
        } catch (error) {
          console.log(error.message)
        }
      }else{
        alert("Please enter reciever")
      }
        
    }

    useEffect(() =>{
        validateEmail()
        console.log(file)
    },[email])


  return (
    <div> <form className="w-full max-w-lg m-auto mt-20 p-2 border-2 rounded-xl border-black " >          
    <div className="flex flex-wrap -mx-3 mb-6">
      <div className="w-full px-3">
        <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-password">
            Reciever
        </label>
        <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="email" type="email" onChange={e => setEmail(e.target.value)} />
        {validated ? <></> :(<label className="block  tracking-wide text-red-500 text-xs  mt-2" > 
            Enter Valid Email
        </label>)  }
      </div>
    </div>
    <div className="flex flex-wrap -mx-3 mb-6">
      <div className="w-full px-3">
        <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-password">
          Subject
        </label>
        <input className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="subject" type="subject" onChange={e => setSubject(e.target.value)} />
          
      </div>
    </div>
    <div className="flex flex-wrap -mx-3 mb-6">
      <div className="w-full px-3">
        <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" for="grid-password">
          Message
        </label>
        <textarea className=" no-resize appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500 h-48 resize-none" id="message" onChange={e => setMessage(e.target.value)}></textarea>
        
      </div>
    </div>
    <div className="md:flex md:items-center">
      <div className="md:w-1/3">
        <>        
        {/* <button className="shadow bg-teal-400 hover:bg-teal-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="button" onClick={handleClick} > */}
          {/* <IoDocumentAttach size={25} /> */}
          <FileBase style={{display:'none'}} type="file" multiple={false} onDone={({ base64 }) => setFile(base64)} />
        {/* </button> */}
        {/* <input type='file' id='file' ref={inputFile} onChange={handleOnChange} style={{display: 'none'}}/> */}

        </>  
      </div>
      <div className="md:w-1/3 space-between">
        <button className="shadow bg-teal-400 hover:bg-teal-400 focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded" type="button" onClick={handleSend}>
          Send
        </button>
      </div>
      <div className="md:w-2/3"></div>
    </div>
  </form></div>
  )
}

export default Form
